import { FieldArray, FieldArrayRenderProps, FormikValues } from 'formik';
import { Typography, TextField, IconButton } from '@mui/material';
import ClearIcon from '@mui/icons-material/Clear';

import { AppBtn } from '../../..';
import { sx } from '../../../BoardManagerModal/BoardColumnsFieldArr/boardColumnsFieldArrStyles';

export const EditorSubtasksFieldArr: React.FC<FormikValues> = ({
   values,
   handleChange,
   handleBlur,
   touched,
   errors,
}) => {
   const placeholders = [
      'e.g. Make coffee',
      'e.g. Drink coffee and smile',
      'e.g. Make coffee to friends',
      'e.g. Invite friends for coffee',
      'e.g. Drink coffee with friends',
   ];

   const addSubtask = (arr: FieldArrayRenderProps) => {
      arr.push('');
   };

   const removeSubtask = (arr: FieldArrayRenderProps, index: number) => {
      arr.remove(index);
   };

   const TextFieldComponent = (index) => {
      return (
         <TextField
            placeholder={
               placeholders[index]
                  ? placeholders[index]
                  : 'e.g. Seems like no time for coffee anymore'
            }
            name={`subtasks.${index}`}
            fullWidth
            id={`subtasks.${index}`}
            value={values.subtasks[index]}
            onChange={handleChange}
            onBlur={handleBlur}
            error={
               touched.subtasks &&
               Boolean(errors.subtasks ? errors.subtasks[index] : '')
            }
            helperText={
               touched.subtasks && errors.subtasks ? errors.subtasks[index] : ''
            }
         />
      );
   };

   // subtasks={props.values.subtasks}
   // value={props.values.subtasks}
   // onChange={props.handleChange}
   // tochedSubtasks={props.touched.subtasks}
   // errorsSubtasks={props.errors.subtasks}

   return (
      <>
         <Typography
            style={sx.columns}
            className='subtasks-heading'
            variant='body2'
         >
            Subtasks
         </Typography>

         <FieldArray
            name='subtasks'
            render={(arrayHelpers) => (
               <div className='subtasks-list'>
                  {values.subtasks.map((_: string, index: number) => (
                     <div key={index} className='subtask-container'>
                        {/* <TextField
                           placeholder={
                              placeholders[index]
                                 ? placeholders[index]
                                 : 'e.g. Seems like no time for coffee anymore'
                           }
                           name={`subtasks.${index}`}
                           fullWidth
                           id={`subtasks.${index}`}
                           value={values.subtasks[index]}
                           onChange={handleChange}
                           onBlur={handleBlur}
                           error={
                              touched.subtasks &&
                              Boolean(
                                 errors.subtasks ? errors.subtasks[index] : '',
                              )
                           }
                           helperText={
                              touched.subtasks && errors.subtasks
                                 ? errors.subtasks[index]
                                 : ''
                           }
                        /> */}
                        <IconButton
                           sx={sx.iconBtn}
                           onClick={() => removeSubtask(arrayHelpers, index)}
                        >
                           <ClearIcon fontSize='small' />
                        </IconButton>
                     </div>
                  ))}
                  <AppBtn
                     onClick={() => addSubtask(arrayHelpers)}
                     buttonSize='small'
                     color='secondary'
                     sx={sx.addBtn}
                  >
                     + Add New Subtask
                  </AppBtn>
               </div>
            )}
         />
      </>
   );
};
