//          return uniqueSubtasks.size === subtasks.length;
//       }),
// });

Yup.addMethod(Yup.mixed, 'uniqueIn', function (array = [], message) {
   return this.test('uniqueIn', message, function (value) {
      return array.filter((item: string) => item === value).length < 2;
   });
});

const schema = Yup.object().shape({
   title: Yup.string().trim().required("Can't be empty"),
   description: Yup.string().trim(),
   subtasks: Yup.array().of(Yup.string().trim().required("Can't be empty"))
   .uniqueIn(''),
});

export const EditorFormik: React.FC<Props> = ({ children }) => {
   const { initialValues, submit } = useFormikValues();

   return (
      <Formik
         initialValues={initialValues}
         validationSchema={schema}
         onSubmit={(values) => submit(values)}
      >
         {children}
      </Formik>
   );
};
