const handleCheckbox = () => {
   const managedSubs = {
      ...managedTask,
      status: values.status,
      subtasks: managedTask.subtasks.map((sub) =>
         values.checked.some((val: string) => val === sub.title)
            ? { ...sub, isCompleted: true }
            : { ...sub, isCompleted: false },
      ),
   };

};
