export const editorStyles = (theme: any) => ({

   '& .MuiTextField-root': {
      position: 'relative'
   },
   '& .MuiOutlinedInput-root': {
      fontSize: 13 / 16 + 'rem',
      'input': {
         py: 1.3
      },
      '& .MuiOutlinedInput-notchedOutline': { 
         borderColor: theme.palette.divider 
      }
   },
   '& .MuiFormHelperText-root': {
      position: 'absolute',
      top: '50%',
      transform: 'translateY(-50%)',
      right: 0,
      my: 0
   },
})
