      },
      h3: {
         fontWeight: 700,
         fontSize: 18 / 16 + 'rem',
         lineHeight: 23 / 18,
      },
      h4: {
         fontWeight: 700,
         fontSize: 15 / 16 + 'rem',
         lineHeight: 19 / 15,
      },
      h5: {
         fontWeight: 700,
         fontSize: 12 / 16 + 'rem',
         lineHeight: 15 / 12,
         letterSpacing: '2.4px',
      },
      body1: {
         fontWeight: 500,
         fontSize: 13 / 16 + 'rem',
         lineHeight: 23 / 13,
      },
      body2: {
         fontWeight: 700,
         fontSize: 12 / 16 + 'rem',
         lineHeight: 15 / 12,
      },
   },
   palette: {
