import { MenuItem } from '@mui/material';


import { FormikValue } from 'formik';
import { Label } from '../..';
import { AppSelect } from '../../..';

type Cols = {
   color: string;
   id: number;
   tasks: {
      id: number;
      completedSubtasks: number;
      title: string;
      description: string;
      status: string;
      subtasks: {
         title: string;
         isCompleted: boolean;
      }[];
   }[];
   name: string;
}[];

interface Props extends FormikValues {
   cols: Cols;
}

export const EditorSelect: React.FC<Props> = ({
   values,
   handleChange,
   cols,
}) => {
   return (
      <>
         <Label>Status</Label>
         {/* <Typography
            style={{ margin: '24px 0 8px' }}
            className='subtasks-heading'
            variant='body2'
         >
            Status
         </Typography> */}

         <AppSelect value={values.status} onChange={handleChange}>
            {cols.map((col: any) => (
               <MenuItem key={col.id} value={col.name}>
                  {col.name}
               </MenuItem>
            ))}
         </AppSelect>
      </>
   );
};
