import { Formik, FormikProps } from 'formik';
import * as Yup from 'yup';

import { useFormikValues, Values } from '.';

type FormikValues = FormikProps<Values>;

type Props = {
   children: (props: FormikValues) => React.ReactNode;
};

declare module 'yup' {
   interface ArraySchema<T> {
      unique(message: string, mapper?: (a: T) => any): this;
   }
}

Yup.addMethod(Yup.array, 'growing', function (message) {
   return this.test('growing', message, function (values) {
      const len = values!.length;
      for (let i = 0; i < len; i++) {
         if (i === 0) continue;
         if (values![i - 1].intervalTime > values![i].intervalTime)
            return this.createError({
               path: `intervals[${i}].intervalTime`,
               message: 'Should be greater than previous interval',
            });
      }
      return true;
   });
});

// Yup.addMethod(Yup.array, 'unique', function (array = [], message) {
//    return this.test('unique', message, function (value) {
//       return array.filter((item:any) => item === value).length < 2;
//    });
// });

// const schema = Yup.object().shape({
//    title: Yup.string().trim().required("Can't be empty"),
//    description: Yup.string().trim(),
//    subtasks: Yup.array()
//       .of(Yup.string().trim().required("Can't be empty"))
//       .uniqueIn('not unique'),
// });

export const EditorFormik: React.FC<Props> = ({ children }) => {
   const { initialValues, submit } = useFormikValues();

   // Yup.addMethod(
   //    Yup.array,
   //    'unique',
   //    function (message, mapper = (a: string) => a) {
   //       return this.test('unique', message, function (list) {
   //          return list?.length === new Set(list?.map(mapper)).size;
   //       });
   //    },
   // );

   const schema = Yup.object().shape({
      title: Yup.string().trim().required("Can't be empty"),
      description: Yup.string().trim(),
      subtasks: Yup.array().of(Yup.string().trim().required("Can't be empty")).unique(''),
      // .unique('contractNum', 'Please provide a unique number.'),
   });

   return (
      <Formik
         initialValues={initialValues}
         validationSchema={schema}
         onSubmit={(values) => submit(values)}
      >
         {children}
      </Formik>
   );
};
